CREATE TABLE stations(
   Name               VARCHAR(17) NOT NULL PRIMARY KEY
  ,Province           VARCHAR(11) NOT NULL
  ,Town               VARCHAR(33) NOT NULL
  ,Address            VARCHAR(57) NOT NULL
  ,CoordenatesXETRS89 VARCHAR(11) NOT NULL
  ,CoordenatesYETRS89 VARCHAR(12) NOT NULL
  ,Latitude           VARCHAR(18) NOT NULL
  ,Longitude          VARCHAR(19) NOT NULL
);
